import React from 'react';
import axios from 'axios';
import{Form, Col} from 'react-bootstrap';
import {Modal, Button} from 'react-bootstrap';

export default class Editproject extends React.Component{

    constructor(props) {
        super(props);
        this.props = {id: '', projectTitle: '', projectDescription: '',  startDate: '',         
        endDate: '',
        googleAutheticationId: 'bAGPgAMaVhcNDO4v554'};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      componentDidMount() {
         let id = this.props.id
        fetch('/TSM/project/list/' + id)
        //fetch('/TSM/project/list/')
            .then(response => {
                return response.json();
            }).then(result => {
                console.log(result);
                this.setState({
                    id:result.id,
                    projectTitle:result.projectTitle,
                    projectDescription:result.projectDescription,
                    startDate:result.startDate,
                    endDate:result.endDate,
                    googleAutheticationId:'bAGPgAMaVhcNDO4v554'
                });
            });
      }
      handleChange(event) {
          const state = this.state
          state[event.target.name] = event.target.value
          this.setState(state);
      }
      handleSubmit(event) {
          event.preventDefault();
          fetch('/TSM/project/add', {
                method: 'PUT',
                body: JSON.stringify({
                                id:this.props.id,
                                projectTitle: this.props.projectTitle,
                                projectDescription: this.props.projectDescription,
                                startDate:this.props.startDate,
                                endDate:this.props.endDate,
                                googleAutheticationId:'bAGPgAMaVhcNDO4v554'

                }),
                headers: {
                                "Content-type": "application/json; charset=UTF-8"
                }
            }).then(response => {
                    if(response.status === 200) {
                        alert("project update successfully.");
                    }
                });
      }



render(){


    return(<div>


<Modal
       {...this.props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
         Edit a Project
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
<div>
    <Form>
  <Form.Row>
    <Form.Group as={Col} controlId="formGridEmail">
      <Form.Label>Project Title</Form.Label>
      <Form.Control type="text" placeholder="Title of Your Project"
       value= {this.props.projectTitle}
       onChange={ this.handleChange}
      />
    </Form.Group>
     <Form.Group as={Col} controlId="formGridPassword">
      <Form.Label>Project Description</Form.Label>
      <Form.Control type="text area" as="textarea" rows="3" placeholder="Project Description" 
      value= {this.props.projectDescription}
      onChange={ this.handleChange} />      
    </Form.Group>
  </Form.Row>
   <Form.Row>
    <Form.Group as={Col} controlId="formGridEmail">
      <Form.Label>Start Date</Form.Label>
      <Form.Control type="date" placeholder="Enter Start date"
       value= {this.props.startDate}
       onChange={ this.handleChange}
      />
    </Form.Group>

    <Form.Group as={Col} controlId="formGridPassword">
      <Form.Label>End Date</Form.Label>
      <Form.Control type="date" placeholder="Enter End Date"
      value= {this.props.endDate}
      onChange={ this.handleChange}
       />
    </Form.Group>
  </Form.Row>
   </Form>
    </div>
      </Modal.Body>
      <Modal.Footer>

     <Button variant='primary' onClick={this.handleSubmit} > submit</Button>

      </Modal.Footer>
    </Modal>




    </div>
    );
}


}
