import React from 'react';
import axios from 'axios';
import{Form, Col} from 'react-bootstrap';
import {Modal, Button} from 'react-bootstrap';

export default class Editproject extends React.Component{

    constructor(props) {
        super(props);
        this.state = {id: '', projectTitle: '', projectDescription: '',  startDate: '',         
        endDate: '',
        googleAutheticationId: 'bAGPgAMaVhcNDO4v554'};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      componentDidMount() {
        console.log(this.props);
        this.setState({
            id: this.props.data ? this.props.data.id : '',
            projectTitle: this.props.data ? this.props.data.projectTitle: '',
            projectDescription:this.props.data ? this.props.data.projectDescription:'',
            startDate:this.props.data ? this.props.data.startDate:'',
            endDate:this.props.data ? this.props.data.endDate:'',
            googleAutheticationId:'bAGPgAMaVhcNDO4v554'
        });
      }
      handleChange(event) {
          // const state = this.state
          // state[event.target.name] = event.target.value
          // this.setState(state);
          this.setState({
            data:{
              ...this.state.data,
              [event.target.name]: event.target.value
            }
          })
      }
      handleSubmit(event) {
          event.preventDefault();
          fetch('/TSM/project/add/', {
                method: 'PUT',
                body: JSON.stringify({
                                id:this.state.id,
                                projectTitle: this.state.projectTitle,
                                projectDescription: this.state.projectDescription,
                                startDate:this.state.startDate,
                                endDate:this.state.endDate,
                                googleAutheticationId:'bAGPgAMaVhcNDO4v554'

                }),
                headers: {
                                "Content-type": "application/json; charset=UTF-8"
                }
            }).then(response => {
                    if(response.status === 200) {
                        alert("project update successfully.");
                    }
                });
      }



render(){
  console.log(this.props.data);


    return(<div>


<Modal
       {...this.props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
         Edit a Project
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
<div>
    <Form>
  <Form.Row>
    <Form.Group as={Col} controlId="formGridEmail">
      <Form.Label>Project Title</Form.Label>
      <Form.Control type="text" placeholder="Title of Your Project"
       value= {this.state.projectTitle}
       onChange={ this.handleChange} name ="same "
      />
    </Form.Group>
     <Form.Group as={Col} controlId="formGridPassword">
      <Form.Label>Project Description</Form.Label>
      <Form.Control type="text area" as="textarea" rows="3" placeholder="Project Description" 
      value = {this.state.projectDescription} name="same1"
      onChange={ this.handleChange} />      
    </Form.Group>
  </Form.Row>
   <Form.Row>
    <Form.Group as={Col} controlId="formGridEmail">
      <Form.Label>Start Date</Form.Label>
      <Form.Control type="date" placeholder="Enter Start date"
       value= {this.state.startDate} name="same2"
       onChange={ this.handleChange}
      />
    </Form.Group>

    <Form.Group as={Col} controlId="formGridPassword">
      <Form.Label>End Date</Form.Label>
      <Form.Control type="date" placeholder="Enter End Date"
      value= {this.state.endDate} name ="same3"
      onChange={ this.handleChange}
       />
    </Form.Group>
  </Form.Row>
   </Form>
    </div>
      </Modal.Body>
      <Modal.Footer>

     <Button variant='primary' onClick={this.handleSubmit} > submit</Button>

      </Modal.Footer>
    </Modal>




    </div>
    );
}


}
