import React from 'react'

import {loadDetails}  from "../actions/index.js/index.js";

import { connect } from 'react-redux';

 class Post extends Component {

  componentDidMount(){
    this.props.loadDetails();
  }

  render() {


// var x =window.location.href;
// console.log(x)
// var y= x.substring(x.lastIndexOf('/') +1)
// y=y.replace(/%20/,' ')
// console.log(y)
// var z= y.substring(y.lastIndexOf('/') +1)
// z=z.replace(/%20/,' ')
// console.log(z)


    const items = this.props.items ? (
     <div className="post"  >
        <h4 className="center">{this.props.items.name}</h4>
        <p>{this.props.items.email}</p>
       </div>
     ) : (
      <div className="center">Loading post of ...</div>
    );

  return (
  <div className="container">

    <h1> {items}</h1>


       </div>
    )
 }
}

const mapStateToProps = (state, ownProps) => {
  let id = ownProps.match.params.item_id;
   return {
    items: state.items.find(item => item.id === id)
  }
 }

 export default connect(mapStateToProps, loadDetails)(Post)
