import React  from 'react'
import axios from 'axios';
import Toolbar from '../dashboard/Toolbar';
import Chart from '../dashboard/Chart.js';
import Bar from './Bar.js';
import Togglebutton from '../dashboard/Togglebutton.js';


export default class ProjectPage  extends React.Component{
 state={
         sideDrawerOpen: true
       };
         drawOpenClickHandler =()=>{
          this.setState((prevState)=> {
         return{ sideDrawerOpen:!prevState.sideDrawerOpen  };

       }
       );

         };


state={
    post: null
}
 async componentDidMount(){

    let id = this.props.match.params.post_id;
     axios.get ('https://jsonplaceholder.typicode.com/users/'+ id )

    .then(res =>{this.setState
         ({post: res.data

        })
        console.log(res)

})

}

render()
{
    let sideDrawerOpen;
  if(this.state.sideDrawerOpen){
    sideDrawerOpen=<Bar/>
   }

    const post = this.state.post ? (
          <div className="post">
           <Toolbar drawerClickHandler ={this.drawOpenClickHandler}/>
           <Bar   show ={sideDrawerOpen}/>
           <Togglebutton/> 
           <Chart/>
           <hr/>
         <h4 className="center"> Name :  {this.state.post.name}</h4>
          <p> Email:   {this.state.post.email}</p>
          <p> Street Name : {this.state.post.address.street}</p>
         </div>
        ) : (
        <div className="center">Loading post...</div>
      );

        return(<div>
           {post}
        </div>);
}
}
