import React from 'react';
import{Card,  CardColumns,Nav} from 'react-bootstrap';
import Createproject from './Createproject.js';
import {Button,ButtonToolbar} from 'react-bootstrap';

export default class Display extends React.Component{
  constructor(props)
  {
      super(props);
      this.state={
          items:[],
          isLoaded: false,
          visible: 3,

      }

      this.loadMore = this.loadMore.bind(this);

  }

  loadMore() {
    this.setState((prev) => {
      return {visible: prev.visible + 2};
    });
  }

  componentDidMount()
  {

      fetch('https://jsonplaceholder.typicode.com/users')


      .then(res=>res.json())
      .then(json => { 
       this.setState({
              isLoaded:true,
              items: json,
                      })

                  });

  }  




render(){
    let addModalClose= ()=> this.setState({addModalShow:false});

var { isLoaded, items} = this.state;
 if(!isLoaded){
     return (<div>Me and My Server Broke Up!</div>);
 }

 else{
return(

    <div className="column-layout">

<CardColumns className="main-column" >
<Card style={{width:'14rem' ,height :'10.70rem'}}>
    <Card.Body>
      <i class="far fa-plus-square fa-3x"></i>

      <Button  onClick={()=> this.setState({addModalShow:true})} variant="danger">create</Button>
    </Card.Body>
  </Card>

  {this.state.items.slice(0, this.state.visible).map((item, index) => {
                return (

                    <Card style={{ width: '14rem'  }}>


     <Card.Body key={item.id}> 
    <Card.Title> {item.name }</Card.Title>
    <Card.Text key={item.id.address}>
          {item.email}
      </Card.Text>
      <Button size="sm" variant="primary">Edit</Button> &nbsp; &nbsp;
         <Button  size="sm" variant="primary">Delete</Button>

 </Card.Body>
</Card>



            );
              })}

</CardColumns>

<div className="part1"></div>
<div className='part2'></div>

<Createproject show={this.state.addModalShow} onHide={addModalClose} />
{this.state.visible < this.state.items.length &&
  <button onClick={this.loadMore} type="button" className="load-more">Load more</button>
}

 </div>



);

 }


}

}
