import  React from 'react';
import{Card,  CardColumns,Nav} from 'react-bootstrap';
import {Button,ButtonToolbar} from 'react-bootstrap';


 export default class Loadmore extends React.Component {
    constructor(props) {
      super(props);

      this.state = {
        items: [],
        visible: 3,
        error: false
      };

      this.loadMore = this.loadMore.bind(this);
    }

    loadMore() {
      this.setState((prev) => {
        return {visible: prev.visible + 2};
      });
    }

    componentDidMount() {
      fetch('https://jsonplaceholder.typicode.com/users').then(
        res => res.json()
      ).then(res => {
        this.setState({
          items: res
        });
      }).catch(error => {
        console.error(error);
        this.setState({
          error: true
        });
      });
    }

    render() {
      return (
        <section className="feed">


          <div className="tiles" aria-live="polite">
              <CardColumns>
            {this.state.items.slice(0, this.state.visible).map((item, index) => {
                return (

                    <Card style={{ width: '14rem'  }}>


     <Card.Body key={item.id}> 
    <Card.Title> {item.name }</Card.Title>
    <Card.Text key={item.id.address}>
          {item.email}
      </Card.Text>
      <Button size="sm" variant="primary">Edit</Button> &nbsp; &nbsp;
         <Button  size="sm" variant="primary">Delete</Button>

 </Card.Body>
</Card>



            );
              })}
         </CardColumns>     
            </div>
            {this.state.visible < this.state.items.length &&
               <button onClick={this.loadMore} type="button" className="load-more">Load more</button>
            }
          </section>
      );
    }
  }
