import React from 'react'

import {loadDetails}  from "../actions/index.js";

import { connect } from 'react-redux';

 class Post extends React.Component {

  componentDidMount(){
    this.props.loadDetails();
  }




// var x =window.location.href;
// console.log(x)
// var y= x.substring(x.lastIndexOf('/') +1)
// y=y.replace(/%20/,' ')
// console.log(y)
// var z= y.substring(y.lastIndexOf('/') +1)
// z=z.replace(/%20/,' ')
// console.log(z)

renderList()
{
    return this.props.items.map(item =>{
        return(
                <div className="item" key={item.id}>
                <i className="large middle aligned icon user"></i>
                <div className="content">
                <div className="description">
                <h2>{item.title}</h2>
                <p>{item.body}</p>
                </div>

                </div>


                </div>
        );
    });
}
render()
{

    return(
        <div className="ui relaxed divided list">
            {this.renderList()}
        </div>
    );
}
}

const mapStateToProps = (state, ownProps) => {
  let id = ownProps.match.params.item.id;
   return {
    item: state.items.find(item=> item.id === id)
  }
 }

 export default connect(mapStateToProps, {loadDetails})(Post)
