import React, {Component} from 'react';
import './App.css';
import {BrowserRouter, Route, Link} from 'react-router-dom';
import Dashboard from './components/dashboard/Dashboard.js';
import ProjectPage from './components/ProjectPage/ProjectPage.js';
import Overview from './components/ProjectPage/Overview.js';






class App extends Component{

  constructor(props)
  {
      super(props);
      this.state={
          items:[],
      }
    }

      async componentDidMount() {
        const items = await  (await fetch(`https://jsonplaceholder.typicode.com/users`)).json();
        this.setState({ items});
      }

render()
{

  const {items} = this.state
  return(
  <div className = "App">
 <BrowserRouter >

<Route  path="/" exact component={Dashboard}/>
<Route   path="/pp" render ={ props => <ProjectPage {...props}  items ={items}/>
}/>

<Route path ={`${url}/:userid`} render={({ match }) => <Overview {...items.find(item=>item.id===match.params.userid)}/>} />

</BrowserRouter>


  </div>
  );
}
}


export default App;
