import React from 'react';
import{Card,  CardColumns} from 'react-bootstrap';
import Createproject from './Createproject.js';

import {Link} from 'react-router-dom';
// import Editproject from './Editproject.js';
import{Form, Col} from 'react-bootstrap';
import {Modal, Button} from 'react-bootstrap';

export default class Display extends React.Component{
  constructor(props)
  {
      super(props);
      this.state={
          items:[],
          isLoaded: true,
          visible: 5,


      }

      this.loadMore = this.loadMore.bind(this);
      this.deleteProject = this.deleteProject.bind(this);  
  }

  loadMore() {
    this.setState((prev) => {
      return {visible: prev.visible + 2};
    });
  }

  async componentDidMount() {
    const items = await  (await fetch(`TSM/project/list`)).json();
    this.setState({ items});
  }


  deleteProject(id) {
		if(window.confirm("Are you sure want to delete?")) {
			fetch('/TSM/project/delete/' + id, {
              method : 'DELETE'
             }).then(response => { 
					if(response.status === 200) {
						alert("project deleted successfully");
            fetch('/TSM/project/all')
						.then(response => {
							return response.json();
						}).then(result => {
							console.log(result);
							this.setState({
								websites:result
							});
						});
					}
			 });
		}
  }


render(){
    let addModalClose= ()=> this.setState({addModalShow:false});
    let addModal_Close= ()=> this.setState({addModal_Show:false});



var { isLoaded} = this.state;
 if(!isLoaded){
     return (<div>No Internet connection!</div>);
 }

 else{
return(
<section>
  <br></br>
  <h4 className="project">Recent Projects</h4>
  <br></br>
    <div className="column-layout">

<CardColumns className="main-column" >
<Card style={{width:'14rem' , height :'9rem'}}>
    <Card.Body>


      <Button  onClick={()=> this.setState({addModalShow:true})} variant="outline-primary"><i className="far fa-plus-square fa-3x"></i></Button>
    </Card.Body>
  </Card>

  {this.state.items.slice(0, this.state.visible).map((item, index) => {
                return (

   <Card border="primary" style={{ width: '14rem'  }}>


     <Card.Body > 

   <Link to={'/'+ item.id} > <Card.Title key={item.id}>  {item.projectTitle }</Card.Title> </Link>
   <hr/>
    <Card.Text >
          {item.projectDescription}

      </Card.Text>
      <Button onClick={()=> this.setState({addModal_Show:true})} size="sm" variant=" outline-primary"><i class="fa fa-pencil-square-o" aria-hidden="true"></i></Button> &nbsp; &nbsp;
         <Button onClick={this.deleteProject.bind(this,item.id)} size="sm" variant=" outline-primary"><i class="fa fa-trash-o" aria-hidden="true"></i></Button> &nbsp; &nbsp;
        <Link to ={'/'+item.id } > <Button   size="sm" variant=" outline-primary"><i class="fas fa-arrow-right"></i></Button> </Link>


 </Card.Body>
</Card>



            );
              })}

</CardColumns>

<div className="part1"></div>
{/* <div className='part2'></div> */}
</div>

<Createproject show={this.state.addModalShow} onHide={addModalClose} />
<Editproject show={this.state.addModal_Show}   onHide={addModal_Close}/>

  {this.state.visible < this.state.items.length &&
  <button  className="load"  onClick={this.loadMore} type="button" className="load-more">Load more</button>
}


 </section>



);

 }


}

}





class Editproject extends React.Component{

  constructor(props) {
      super(props);
      this.state = {id: '', projectTitle: '', projectDescription: '',  startDate: '',         
      endDate: '',
      googleAutheticationId: 'bAGPgAMaVhcNDO4v554'};
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount(id) {
       //let id = this.props.match.params.uid
      fetch('/TSM/project/list/' + id)
      //fetch('/TSM/project/list/')
          .then(response => {
              return response.json();
          }).then(result => {
              console.log(result);
              this.setState({
                  id:result.id,
                  projectTitle:result.projectTitle,
                  projectDescription:result.projectDescription,
                  startDate:result.startDate,
                  endDate:result.endDate,
                  googleAutheticationId:'bAGPgAMaVhcNDO4v554'
              });
          });
    }
    handleChange(event) {
        const state = this.state
        state[event.target.name] = event.target.value
        this.setState(state);
    }
    handleSubmit(event) {
        event.preventDefault();
        fetch('/TSM/project/add', {
              method: 'PUT',
              body: JSON.stringify({
                              id:this.state.id,
                              projectTitle: this.state.projectTitle,
                              projectDescription: this.state.projectDescription,
                              startDate:this.state.startDate,
                              endDate:this.state.endDate,
                              googleAutheticationId:'bAGPgAMaVhcNDO4v554'

              }),
              headers: {
                              "Content-type": "application/json; charset=UTF-8"
              }
          }).then(response => {
                  if(response.status === 200) {
                      alert("project update successfully.");
                  }
              });
    }



render(){


  return(<div>


<Modal
     {...this.props}
    size="lg"
    aria-labelledby="contained-modal-title-vcenter"
    centered>
    <Modal.Header closeButton>
      <Modal.Title id="contained-modal-title-vcenter">
       Edit a Project
      </Modal.Title>
    </Modal.Header>
    <Modal.Body>
<div>
  <Form>
<Form.Row>
  <Form.Group as={Col} controlId="formGridEmail">
    <Form.Label>Project Title</Form.Label>
    <Form.Control type="text" placeholder="Title of Your Project"
     value= {this.state.projectTitle}
     onChange={ this.handleChange}
    />
  </Form.Group>
   <Form.Group as={Col} controlId="formGridPassword">
    <Form.Label>Project Description</Form.Label>
    <Form.Control type="text area" as="textarea" rows="3" placeholder="Project Description" 
    value= {this.state.projectDescription}
    onChange={ this.handleChange} />      
  </Form.Group>
</Form.Row>
 <Form.Row>
  <Form.Group as={Col} controlId="formGridEmail">
    <Form.Label>Start Date</Form.Label>
    <Form.Control type="date" placeholder="Enter Start date"
     value= {this.state.startDate}
     onChange={ this.handleChange}
    />
  </Form.Group>

  <Form.Group as={Col} controlId="formGridPassword">
    <Form.Label>End Date</Form.Label>
    <Form.Control type="date" placeholder="Enter End Date"
    value= {this.state.endDate}
    onChange={ this.handleChange}
     />
  </Form.Group>
</Form.Row>
 </Form>
  </div>
    </Modal.Body>
    <Modal.Footer>

   <Button variant='primary' onClick={this.handleSubmit} > submit</Button>

    </Modal.Footer>
  </Modal>




  </div>
  );
}


}
